generated.css:
*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

html {
  font-size: 62.5%;
}

body {
  font-family: "Josefin Sans", sans-serif;
  font-size: 1.6rem;
  background: #23272b;
  font-weight: 300;
  line-height: 1.6;
  color: #84888d;
  box-sizing: border-box;
  text-align: center;
}
body::after {
  content: "";
  display: block;
  clear: both;
}

.container {
  width: calc(100% - 6rem);
  float: left;
  overflow: hidden;
  padding: 0 2rem;
}
@media (min-width: 768px) {
  .container {
    width: calc(100% - 15rem);
  }
}

input {
  display: block;
  font-family: inherit;
  margin: 0 auto 2rem auto;
  width: 100%;
  font-size: 1.6rem;
  font-weight: 400;
  color: #afb8bf;
  max-width: 400px;
  padding: 1.4rem;
  background: #2e3439;
  outline: none;
  border: 1px solid #27272c;
}
@media (min-width: 768px) {
  input {
    padding: 2rem;
    font-size: 2rem;
  }
}

a {
  color: inherit;
  text-decoration: none;
}

section {
  padding: 5rem 0;
}
section::after {
  content: "";
  display: block;
  clear: both;
}

.heading-primary {
  font-size: 3rem;
  font-weight: 700;
  color: #39ae44;
  margin: 0 auto 2.6rem auto;
}
.heading-primary::after {
  content: "";
  display: block;
  width: 10rem;
  height: 2px;
  background: #84888d;
  margin: 2rem auto 0 auto;
}
@media (min-width: 768px) {
  .heading-primary {
    margin-bottom: 4.4rem;
    font-size: 4.4rem;
  }
}
.heading-primary--main {
  color: #afb8bf;
}
.heading-primary--sub {
  font-size: 1.5rem;
}

.descriptor {
  display: block;
  width: 50%;
  min-width: 26rem;
  margin: 0 auto;
}
@media (min-width: 768px) {
  .descriptor {
    font-size: 2rem;
  }
}
.descriptor + a {
  margin-top: 2rem;
}
.descriptor + a:not(:last-child) {
  margin-right: 2rem;
}

.btn, .btn:link, .btn:visited {
  display: inline-block;
  outline: none;
  padding: 1.5rem 2.5rem;
  text-decoration: none;
  border-radius: 10rem;
  font-size: 1.4rem;
  color: #23272b;
  background: #39ae44;
  border: 1px solid #39ae44;
  font-family: inherit;
  transition: all 0.2s;
}
@media (min-width: 768px) {
  .btn, .btn:link, .btn:visited {
    font-size: 1.8rem;
  }
}
.btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 1rem 2rem rgba(0, 0, 0, 0.2);
}
.btn:active {
  transform: translateY(-1px);
  box-shadow: 0 1rem 2rem rgba(46, 52, 57, 0.2);
}

.header {
  padding: 5rem 0;
}

.expense {
  background: #2e3439;
  border-left: 5px solid #44494e;
  padding: 1rem 1.5rem;
  max-width: 800px;
  text-align: left;
  margin: 0 auto;
  margin-bottom: 2rem;
}
@media (min-width: 410px) {
  .expense {
    padding: 1.5rem 2rem;
    max-width: 500px;
  }
}
@media (min-width: 768px) {
  .expense {
    padding: 3rem;
    max-width: 600px;
  }
}
@media (min-width: 900px) {
  .expense {
    max-width: 750px;
  }
}
@media (min-width: 410px) {
  .expense {
    margin-bottom: 2.3rem;
  }
}
@media (min-width: 768px) {
  .expense {
    margin-bottom: 3.5rem;
  }
}
.expense__header::after {
  content: "";
  display: block;
  clear: both;
}
.expense__name {
  margin-bottom: 0.5rem;
  float: left;
  font-size: 1.6rem;
  font-weight: 400;
}
@media (min-width: 410px) {
  .expense__name {
    font-size: 1.8rem;
  }
}
@media (min-width: 768px) {
  .expense__name {
    font-size: 2.4rem;
  }
}
.expense__label {
  font-size: 1rem;
  text-transform: uppercase;
  color: #636a71;
}
@media (min-width: 410px) {
  .expense__label {
    font-size: 1.2rem;
  }
}
@media (min-width: 768px) {
  .expense__label {
    font-size: 1.6rem;
  }
}
.expense__amount {
  float: right;
  margin-top: 0.2rem;
  font-size: 1.4rem;
  color: #39ae44;
  font-weight: 500;
}
@media (min-width: 410px) {
  .expense__amount {
    font-size: 1.6rem;
    margin-top: 0.5rem;
  }
}
@media (min-width: 768px) {
  .expense__amount {
    font-size: 2rem;
  }
}
.expense__description {
  font-size: 1.4rem;
  color: #636a71;
}
@media (min-width: 410px) {
  .expense__description {
    font-size: 1.6rem;
    letter-spacing: 0.2px;
  }
}
@media (min-width: 768px) {
  .expense__description {
    margin-top: 1rem;
    font-size: 1.8rem;
  }
}

.nav {
  position: relative;
  width: 6rem;
  height: 100vh;
  float: left;
}
@media (min-width: 768px) {
  .nav {
    width: 15rem;
  }
}
.nav__container {
  position: fixed;
  height: 100vh;
  width: 6rem;
  background: #16191c;
}
@media (min-width: 768px) {
  .nav__container {
    width: 15rem;
  }
}
.nav__items {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  list-style: none;
}
.nav__item {
  display: block;
  width: 100%;
  color: #84888d;
  text-decoration: none;
  text-transform: uppercase;
  padding: 1rem 0;
  transition: all 0.2s;
  border-left: 3px solid transparent;
}
@media (min-width: 768px) {
  .nav__item {
    font-size: 1.8rem;
  }
}
.nav__item:hover {
  background: #23272b;
  border-left: 3px solid #84888d;
}
.nav__item--active {
  background: #23272b;
  border-left: 3px solid #84888d;
}

.form {
  padding: 5rem 0;
}
.form__button {
  margin-bottom: 2rem;
}

.expenses a {
  margin-bottom: 2rem;
}

.show {
  padding: 5rem 0;
}
@media (min-width: 768px) {
  .show {
    padding: 10rem 0;
  }
}
.show__container {
  background-color: #2e3439;
  padding: 5rem 0;
  max-width: 70rem;
  margin: auto;
}
.show .descriptor {
  width: 80%;
}
.show span {
  color: #39ae44;
}

.not-found {
  text-align: center;
}
@media (min-width: 768px) {
  .not-found__title {
    font-size: 10rem;
    color: #39ae44;
    font-weight: 400;
  }
}
@media (min-width: 768px) {
  .not-found__subtitle {
    font-size: 3rem;
  }
}
